import React, {Component} from 'react';
import axios from 'axios';

// const Aux =(prop)=>{
//     return (
//         <div>prop.title</div>
//     )
// }


class ConnectBackend extends Component {

    //re define the state
    state={
        posts:[],
    }

    // get function and setstate
    componentDidMount () {
        const randomStart=Math.floor(Math.random()*3)
        axios.get( 'https://jsonplaceholder.typicode.com/photos' )
            .then( response => {
                const posts = response.data.slice(randomStart, randomStart+2);
                const updatedPosts = posts.map(post => {
                    return {
                        ...post,
                        author: 'Max'
                    }
                });
                this.setState({posts: updatedPosts});
                console.log(updatedPosts);
            }).catch(
                console.log(Error)
            );
    }

    // post function function

    render(){
        const kkkposts = this.state.posts.map(post => {
            return (<div>
                        <div key={post.id}>{post.title}</div>
                        <img src={post.url} alt="" />
                    </div>
                )
        });



    return (
    <div>
        <div>{kkkposts}</div>
        <div>hello</div>
        <button onClick={()=>this.componentDidMount()}>change</button>
    </div>
    )
    }
}

export default ConnectBackend;
